import React, { useState } from 'react';
import io from 'socket.io-client';

const socket = io('http://localhost:4000');

function App() {
  const [gameId, setGameId] = useState('');
  const [role, setRole] = useState('player');
  const [status, setStatus] = useState('');
  const [game, setGame] = useState(null);

  const joinGame = () => {
    socket.emit('joinGame', { gameId, role });
  };

  React.useEffect(() => {
    socket.on('joined', (data) => {
      setStatus(`Joined as ${data.role}`);
    });
    socket.on('full', (data) => {
      setStatus(`Cannot join: ${data.reason}`);
    });
    socket.on('update', (gameData) => {
      setGame(gameData);
    });
    return () => {
      socket.off('joined');
      socket.off('full');
      socket.off('update');
    };
  }, []);

  return (
    <div style={{ padding: 32, fontFamily: 'sans-serif' }}>
      <h1>Battleships Online</h1>
      <div>
        <input
          placeholder="Game ID"
          value={gameId}
          onChange={e => setGameId(e.target.value)}
        />
        <select value={role} onChange={e => setRole(e.target.value)}>
          <option value="player">Player</option>
          <option value="observer">Observer</option>
        </select>
        <button onClick={joinGame}>Join Game</button>
      </div>
      <div style={{ marginTop: 16 }}>
        <b>Status:</b> {status}
      </div>
      {game && (
        <div style={{ marginTop: 16 }}>
          <b>Players:</b> {game.players.length} / 2<br />
          <b>Observers:</b> {game.observers.length} / 10<br />
          <b>Game State:</b> {game.state}
        </div>
      )}
    </div>
  );
}

export default App;
